import React, {Component} from 'react';
import { Breadcrumb, BreadcrumbItem, Row,
 Button, Form, FormGroup, Label, Input, Col, FormFeedback } from 'reactstrap';
import { Link } from 'react-router-dom';
import { render } from '@testing-library/react';

class Contact extends Component{

  constructor(props){
    super(props);

    this.state = {
      firstName : '',
      lastName  : '',
      telNum : '',
      email : '',
      agree : '',
      contactType : '',
      message : '',
      touched : {
        firstName : false,
        lastName : false,
        telName : false,
        email : false
      }
    }

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleBlur = this.handleBlur.bind(this);

  }

  handleInputChange(event){

    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name] : value
    });

  }

  handleSubmit(event){
    console.log("Current state is: " +JSON.stringify(this.state));
    alert("Current state is: " +JSON.stringify(this.state));
    event.preventDefault();

  }

  handleBlur = (field) => (evt) => {

    this.setState({
      touched : { ...this.state.touched, [field]: true}
    })

  };

  validate(firstName, lastName, email, telName){

    const errors = {
      firstName : '',
      lastName  : '',
      telNum : '',
      email : '',
    }

    if(this.state.touched.firstName && firstName.length < 3)
      errors.firstName = 'First name should be greater then equal to three';
    else if(this.state.touched.firstName && firstName.length < 10)
      errors.firstName = 'First name should be less then 10 chars';

    if(this.state.touched.lastName && lastName.length < 3)
      errors.lastName = 'Last name should be greater then equal to three';
    else if(this.state.touched.lastName && lastName.length < 10)
      errors.lastName = 'Last name should be less then 10 chars';

    return errors;
  }

  render(){

    const error = this.validate(this.state.firstName, this.state.lastName);

    return (
      <div className="container">
        <Row>
          <Breadcrumb>
            <BreadcrumbItem><Link to="/home">Home</Link></BreadcrumbItem>
            <BreadcrumbItem active>Contact Us</BreadcrumbItem>
          </Breadcrumb>
          <div className="col-12">
            <h3>Contact Us</h3>
            <hr />
          </div>
        </Row>

        <div className="row row-content">
          <div className="col-12">
            <h3>Location Information</h3>
          </div>
          <div className="col-12 col-sm-4 offset-sm-1">
            <h5>Our Address</h5>
            <address>
              121, Clear Water Bay Road
              <br />
              Clear Water Bay, Kowloon
              <br />
              HONG KONG
              <br />
              <i className="fa fa-phone" />: +852 1234 5678
              <br />
              <i className="fa fa-fax" />: +852 8765 4321
              <br />
              <i className="fa fa-envelope" />:{" "}
              <a href="mailto:confusion@food.net">confusion@food.net</a>
            </address>
          </div>
          <div className="col-12 col-sm-6 offset-sm-1">
            <h5>Map of our Location</h5>
          </div>
          <div className="col-12 col-sm-11 offset-sm-1">
            <div className="btn-group" role="group">
              <a
                role="button"
                className="btn btn-primary"
                href="tel:+85212345678"
              >
                <i className="fa fa-phone" /> Call
              </a>
              <a role="button" className="btn btn-info">
                <i className="fa fa-skype" /> Skype
              </a>
              <a
                role="button"
                className="btn btn-success"
                href="mailto:confusion@food.net"
              >
                <i className="fa fa-envelope-o" /> Email
              </a>
            </div>
          </div>
        </div>
        <div className="row row-content">
          <div className="col-12">
            <h3>Send us your feedback</h3>
          </div>
          <div className="col-12 col-md-9">
            <Form onSubmit={this.handleSubmit} >
              <FormGroup row>
                <Label htmlFor="firstName" md={2}>First Name</Label>
                <Col md={10}>
                  <Input type="text" id="firstName" name="firstName" placeholder="First Name" 
                     value={this.state.firstName}
                     onChange={this.handleInputChange} 
                     onBlur={this.handleBlur('firstName')} />
                  <FormFeedback>{errors.firstName}</FormFeedback>
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label htmlFor="lastName" md={2}>last Name</Label>
                <Col md={10}>
                  <Input type="text" id="lastName" name="lastName" placeholder="Last Name" 
                     value={this.state.lastName} 
                     onChange={this.handleInputChange} />
                  <FormFeedback>{errors.lastName}</FormFeedback>
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label htmlFor="telName" md={2}>Contact tel number</Label>
                <Col md={10}>
                  <Input type="text" id="telName" name="telName" placeholder="Tel number" 
                     value={this.state.telName} 
                     onChange={this.handleInputChange} />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label htmlFor="email" md={2}>email</Label>
                <Col md={10}>
                  <Input type="text" id="email" name="email" placeholder="email" 
                     value={this.state.email} 
                     onChange={this.handleInputChange} />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Col md={{size:6, offset:2}}>
                  <FormGroup check>
                    <Label check>
                      <Input type="checkbox" name="agree" checked={this.state.agree} 
                       onChange={this.handleInputChange} />  {'  '}
                      <strong> May we contact you?</strong>
                    </Label>
                  </FormGroup>
                  </Col>
                <Col md={{size:3, offset:1}}>
                  <Input type="select" name="contactType" value={this.state.contactType}
                   onChange={this.handleInputChange} >
                    <option>Tel</option>
                    <option>Email</option>
                  </Input>
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label htmlFor="feedback" md={2}>Your Feedback</Label>
                <Col md={10}>
                  <Input type="textarea" id="message" name="message" placeholder="Your Feedback" 
                     rows="12" value={this.state.message} 
                     onChange={this.handleInputChange} />
                </Col>
              </FormGroup>
              <FormGroup>
                <FormGroup row>
                  <Col md={{size:10, offset:2}}>
                    <Button type="submit" color="primary">
                      Send Feedback
                    </Button>
                  </Col>
                </FormGroup>
              </FormGroup>
            </Form>
          </div>
        </div>
      </div>
    );

  }

}

export default Contact;
