import React, { useEffect, useState } from 'react';
import { 
    Row, 
    Card, 
    CardImg, 
    CardTitle, 
    CardBody, 
    CardText, 
    CardSubtitle, 
    Col, 
    Container
  } from 'reactstrap';
import CardLayout from '../Index/CardForCategory';

function WorldNews(){

    const [worldNews,setWorldNews] = useState([]);

    useEffect( () => {

      this.fetchCategoryData("World");
    }, []);

    this.fetchCategoryData( (category) => {
        const response = fetch(`http://localhost:8085/news/${category}`, {
            method: "GET",
            cache: "no-cache",
            headers: { "Content-Type": "application/json" },
            })
            .then( response => {
              if(response.ok){
                return response.json();
              }else{
                let error = response.status;
                throw error;}
            }).then( object => setWorldNews(object) );
    })


    return(
      <CardLayout props={worldNews} />
    );
}
export default WorldNews;
