import React, { useEffect, useState } from 'react';
import { 
    Row, 
    Card, 
    CardImg, 
    CardTitle, 
    CardBody, 
    CardText, 
    CardSubtitle, 
    Col, 
    Container
  } from 'reactstrap';
import CardLayout from '../Index/CardForCategory';

function SportsNews(){

    const [sportsNews,setSportsNews] = useState([]);

    useEffect( () => {

      this.fetchCategoryData("sports");
    }, []);

    this.fetchCategoryData( (category) => {
        const response = fetch(`http://localhost:8085/news/${category}`, {
            method: "GET",
            cache: "no-cache",
            headers: { "Content-Type": "application/json" },
            })
            .then( response => {
              if(response.ok){
                return response.json();
              }else{
                let error = response.status;
                throw error;}
            }).then( object => setSportsNews(object) );
    });

    return(
        <CardLayout props={sportsNews} />
    );
}
export default SportsNews;
