import React, { useEffect, useState } from 'react';
import { 
    Row, 
    Container
  } from 'reactstrap';
import CardLayout from '../Index/Card';
import Pagination from '../Util/PaginationComponent';
import Constants from '../Util/Constants';

function TopNews(){

  const [topNews,setTopNews] = useState([]);

  useEffect( () => {
    fetchTopNews(); 
  },[]);

  let fetchTopNews = () => {
    let pageNum = 1;
    const response = fetch(`${Constants.BASEURL}/news/top-news/${pageNum}`, {
      method: "GET",
      cache: "no-cache",
      headers: { "Content-Type": "application/json" },
      })
      .then( response => {
        if(response.ok){
          return response.json();
        }else{
          let error = response.status;
          throw error;}
    }).then( object => setTopNews(object) );
  }

  return(
    <div>
      <Container>
        <Row>
          {topNews.map(news =><CardLayout topNews={news} /> )}
        </Row>
      </Container>
    </div>
  )
}

export default TopNews;
