import React, { useEffect, useState } from 'react';
import { 
    Row, 
    Container
  } from 'reactstrap';
import CardLayout from '../Index/Card';
import Pagination from '../Util/PaginationComponent';

function TopNews(){

  const [topNews,setTopNews] = useState([]);

  useEffect( () => {

    const response = fetch(`http://localhost:8085/news/top-news`, {
      method: "GET",
      cache: "no-cache",
      headers: { "Content-Type": "application/json" },
      })
      .then( response => {
        if(response.ok){
          return response.json();
        }else{
          let error = response.status;
          throw error;}
      }).then( object => setTopNews(object) );

  },[topNews]);

  return(
    <div>
      <Container>
        <Row>
          {topNews.map(news =><CardLayout topNews={news} /> )}
        </Row>
      </Container>
      <Pagination props={topNews} />
    </div>
  )
}

export default TopNews;
