import React, { useEffect, useState } from 'react';
import { 
    Row, 
    Card, 
    CardImg,  
    CardBody, 
    Col, 
    Container
  } from 'reactstrap';

function TopNews(){

  const [topNews,setTopNews] = useState([]);

  useEffect( () => {

    const response = fetch(`http://localhost:8085/news/top-news`, {
      method: "GET",
      cache: "no-cache",
      headers: { "Content-Type": "application/json" },
      })
      .then( response => {
        if(response.ok){
          return response.json();
        }else{
          let error = response.status;
          throw error;}
      }).then( object => setTopNews(object) );

  },[]);

  return(

    <div>
    <Container>
      <Row>
        <Col xs="4" className ="mt-10 mb-10" >
          <Card>
            <CardImg top src={topNews.pic_src} alt="">
            </CardImg>
            <CardBody>
              <a href={topNews.link}>
               {topNews.title}
              </a>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container> 
    </div>
  )
}

export default TopNews;
